function Board(props) {

  const renderBoard = () => {
    let cellNumber = props.boardSize;
    let rowCount = columnCount = props.boardSize / props.boardSize;
    for (let i = 0; i < rowCount; i++) {
      let rowCells = [];
      for (let j = 0; j < columnCount; j++) {
        rowCells.push(
          <div className="cell" key={cellNumber}>
            {cellNumber === props.curPosition ? <div className="player-token"></div> : null}
            <div>{cellNumber--}</div>
          </div>
        );
      }
      if (i % 2 !== 0) {
        rowCells.reverse();
      }
    }
  };

  return (
    <div className="board">{renderBoard()}</div>
  );
}

export default Board;
