import React, { useState } from 'react';

function Game(props) {

  const [position, setPosition] = useState(1);

  const generateRandomNumber = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  };

  const rollDice = () => {
    const diceValue = generateRandomNumber(1, 6);
    const newPosition = position + diceValue;

    if (newPosition > props.boardSize * props.boardSize) {
      alert('Congratulations! You won!');
      setPosition(1);
    } else {
      setPosition(newPosition);
    }
  };

  const renderBoard = () => {
    let cellNumber = boardSize * boardSize;

    for (let i = 0; i < boardSize; i++) {
      let rows = [];
      for (let j = 0; j < boardSize; j++) {
        rows.push(
          <div className="cell" key={cellNumber}>
            {cellNumber === position ? <div className="player-token"></div> : null}
            <div>{cellNumber--}</div>
          </div>
        );
      }
      if (i % 2 !== 0) {
        rows.reverse();
      }
      cells.push(
        <div className="row" key={i}>
          {rows}
        </div>
      );
    }
    return cells;
  };

  return (
    <div className='game'>
      <div className="board">{renderBoard()}</div>
      <div className="controls">
        <button onClick={rollDice}>Roll Dice</button>
        <div className="player">Player Position: {position}</div>
      </div>
    </div>
  );
}

export default Game;
