import Cell from "./Cell";
import Row from "./Row";

function Board({ boardSize, curPosition }) {

  const renderBoard = () => {
    let cellNumber = boardSize;
    let row = Math.sqrt(boardSize);

    let rows = [];
    for (let i = 0; i < halfBoardSize; i++) {
      let rowCells = [];
      for (let j = 0; j < halfBoardSize; j++) {
        rowCells.push(
          <Cell key={cellNumber} cellNumber={cellNumber} hasPlayer={cellNumber === curPosition} />
        );
        cellNumber--;
      }
      rows.push(<Row rowCount={i} key={i}>{rowCells}</Row>);
    }
    return rows;
  };

  return (
    <div className="board">{renderBoard()}</div>
  );
}

export default Board;
