import React, { useState, useEffect, useMemo } from 'react';
import Board from "./Board";
import Dice from "./Dice";

export default function Game({ boardSize, crookedDice }) {
  const initPosition = 0;
  const [position, setPosition] = useState(initPosition);
  const [winner, setWinner] = useState(false);

  const snakes = useMemo(() => [{
    startPos: 98,
    endPos: 2,
    color: "green",
  }, {
    startPos: 53,
    endPos: 38,
    color: "blue",
  }, {
    startPos: 37,
    endPos: 14,
    color: "brown",
  }, {
    startPos: 72,
    endPos: 56,
    color: "purple",
  }], []);
  const checkForSnake = () => {
    const _snake = snakes.find((snake) => {
      return snake.startPos === position;
    });
    if (_snake) {
      setPosition(_snake.endPos);
    }
  }
  useEffect(() => {


    const timer = setTimeout(() => {
      checkForSnake();
    }, 1000);
    return () => clearTimeout(timer);
  }, [position, checkForSnake]);

  const cellsCount = boardSize * boardSize;

  const checkWinner = (diceNum) => {
    const newPosition = position + diceNum;
    if (newPosition > cellsCount) {
      alert('You need only ' + (cellsCount - position) + ' to win. Please try again!');
      return false;
    }
    setPosition(newPosition);
    return newPosition === cellsCount;
  }

  const onDiceRoll = (diceNum) => {
    if (position === 0) {
      if (diceNum !== 6) {
        return;
      }
      diceNum = 1;
    }
    const winner = checkWinner(diceNum);
    if (winner) setWinner(winner);
  }

  const resetGame = () => {
    setPosition(initPosition);
    setWinner(false);
  }

  const renderCongrats = () => {
    return (<div>
      <div>Congratulations! You won!!</div>
      <button onClick={resetGame}>Reset Game</button>
    </div>);
  }

  return (
    <div className='game'>
      <Board boardSize={boardSize} snakes={snakes} curPosition={position} />
      {winner ? renderCongrats() : <Dice onDiceRoll={onDiceRoll} isCrooked={crookedDice} />}
    </div>
  );
}
