import Cell from "./Cell";
import Row from "./Row";

function Board({ boardSize }) {

  const renderBoard = () => {
    let cellNumber = boardSize;
    let halfBoardSize = boardSize / boardSize;

    for (let i = 0; i < halfBoardSize; i++) {
      let rowCells = [];
      for (let j = 0; j < halfBoardSize; j++) {
        rowCells.push(
          <Cell cellNumber={cellNumber} hasPlayer={cellNumber === curPosition} />
        );
      }
      <Row rowCount={i} >{rowCells}</Row>
    }
  };

  return (
    <div className="board">{renderBoard()}</div>
  );
}

export default Board;
