import React, { useState } from 'react';
import './App.css';

const boardSize = 10;
const numSnakes = 5;
const numLadders = 5;
const diceValue = 

function App() {
  const [position, setPosition] = useState(1);

  const generateRandomNumber = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  };

  const generateSnakesAndLadders = () => {
    const snakes = Array.from({ length: numSnakes }, () => generateRandomNumber(5, boardSize - 1));
    const ladders = Array.from({ length: numLadders }, () => generateRandomNumber(2, boardSize - 5));

    return { snakes, ladders };
  };

  const { snakes, ladders } = generateSnakesAndLadders();

  const checkSnakeOrLadder = (position) => {
    const snakeIndex = snakes.indexOf(position);
    const ladderIndex = ladders.indexOf(position);

    if (snakeIndex !== -1) {
      setPosition(position - generateRandomNumber(1, 3));
    } else if (ladderIndex !== -1) {
      setPosition(position + generateRandomNumber(1, 3));
    } else {
      setPosition(position);
    }
  };

  const rollDice = () => {
    const diceValue = generateRandomNumber(1, 6);
    const newPosition = position + diceValue;

    if (newPosition > boardSize * boardSize) {
      alert('Congratulations! You won!');
      setPosition(1);
    } else {
      checkSnakeOrLadder(newPosition);
    }
  };

  const renderBoard = () => {
    let cells = [];
    let cellNumber = boardSize * boardSize;

    for (let i = 0; i < boardSize; i++) {
      let row = [];
      for (let j = 0; j < boardSize; j++) {
        let cellContent = cellNumber;
      if (snakes.includes(cellNumber)) {
        cellContent = <div className="snake">{cellNumber}</div>;
      } else if (ladders.includes(cellNumber)) {
        cellContent = <div className="ladder">{cellNumber}</div>;
      }
        row.push(
          <div className="cell" key={cellNumber}>
            {cellNumber === position ? <div className="player-token"></div> : null}
            {cellContent}
          </div>
        );
        cellNumber--;
      }
      if (i % 2 !== 0) {
        row.reverse();
      }
      cells.push(
        <div className="row" key={i}>
          {row}
        </div>
      );
    }
    return cells;
  };

  return (
    <div className="App">
      <h1>Snake and Ladder Game</h1>
      <div className='game'>
        <div className="board">{renderBoard()}</div>
        <div className="controls">
          <button onClick={rollDice}>Roll Dice</button>
          <div className='dice-num'>Dice Number: {diceValue}</div>
          <div className="player">Player Position: {position}</div>
        </div>
      </div>
    </div>
  );
}

export default App;
