import Cell from "./Cell";
import Row from "./Row";

export default function Board({ boardSize, curPosition }) {

  const renderCells = () => {
    let cellsCount = boardSize * boardSize;
    let rows = [];
    for (let i = 0; i < boardSize; i++) {
      let rowCells = [];
      for (let j = 0; j < boardSize; j++) {
        rowCells.push(
          <Cell key={cellsCount} cellNumber={cellsCount} hasPlayer={cellsCount === curPosition} />
        );
        cellsCount--;
      }
      rows.push(<Row rowCount={i}>{rowCells}</Row>);
    }
    return rows;
  }

  const renderBoard = () => {
    renderSnakes();
    renderCells();
  };

  return (
    <div className="board">{renderBoard()}</div>
  );
}
