import React, { useState } from 'react';
import Board from "./Board";
import Dice from "./Dice";

export default function Game({ boardSize, crookedDice }) {
  const initState = 0;

  const [position, setPosition] = useState(initState);

  const isPlayerActive = (diceNum) => {

    if (position === 0) {
      if (diceNum !== 6) {
        return;
      } else {
        diceNum = 1;
      }
    }
  }

  const onDiceRoll = (diceNum) => {
    if (isPlayerActive(diceNum)) {}

    const newPosition = position + diceNum;
    console.log("New Position: ", newPosition);
    if (newPosition === boardSize) {
      setPosition(newPosition);
    } else if (newPosition > boardSize) {
      alert(diceNum + ' is more than what you need to win. Please try again!')
    } else {
      setPosition(newPosition);
    }
  }

  return (
    <div className='game'>
      <Board boardSize={boardSize} curPosition={position} />
      {position === boardSize
        ? (<div><div>Congratulations! You won!!</div>
          <button onClick={() => setPosition(initState)}>Reset Game</button></div>) :
        <Dice onDiceRoll={onDiceRoll} isCrooked={crookedDice} />}
    </div>
  );
}
