import React, { useState } from 'react';
import Board from "./Board";
import Dice from "./Dice";

export default function Game({ boardSize, crookedDice }) {
  const initState = 0;

  const [position, setPosition] = useState(initState);

  const onDiceRoll = (newDiceNum) => {
    if (position === 0) {
      if (newDiceNum !== 6) {
        return;
      } else {
        newDiceNum = 1;
      }
    }
    const newPosition = position + newDiceNum;
    console.log("New Position: ", newPosition);
    if (newPosition === boardSize) {
      setPosition(newPosition);
    } else if (newPosition > boardSize) {
      alert(newDiceNum, ' is more than what you need to win. Please try again!')
    } else {
      setPosition(newPosition);
    }
  }

  return (
    <div className='game'>
      <Board boardSize={boardSize} curPosition={position} />
      {position === boardSize 
      ? (<div>Congratulations! You won!!</div>
        ) :
        <Dice onDiceRoll={onDiceRoll} isCrooked={crookedDice} />}
    </div>
  );
}
